package javaprogram26;

public class Naturalnumber2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i,num = 100, sum=0;
        for (i=1; i<=num; ++i) {
        	sum = sum+i;
        }
         System.out.println("The sum of first 100 natural number is =" +sum);
	}

}
