import java.util.*;
public class Pattern_1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     int n = 4;
     int m = 5;
     //outer look
     for(int i=1; i<=n; i++) {
    	 //ineer loop
    	  for( int j=1; j<=m; j++) {
    		  System.out.print("*");
    	  }
    	  System.out.println(" ");
     }
    	  }
     }
